contracts/SwapRouter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

interface IAMMRouter {
  function swapExactTokensForTokens(
    uint amountIn,
    uint amountOutMin,
    address[] calldata path,
    address to,
    uint deadline
  ) external returns (uint[] memory amounts);
}

contract SwapRouter {
  using SafeERC20 for IERC20;

  IAMMRouter public immutable ammRouter;
  address public owner;

  event SwapExecuted(
    address indexed user,
    uint amountIn,
    uint amountOut,
    address[] path
  );

  modifier onlyOwner(){
    require(msg.sender == owner, "Not Owner");
    _;
  }

  constructor(address _ammRouter){
    ammRouter = IAMMRouter(_ammRouter);
    owner = msg.sender;
  }

  function swapExactTokensForTokens(
    uint amountIn,
    uint amountOutMin,
    address[] calldata path,
    uint deadline
  ) external returns(uint[] memory amounts){

    // Pull funds from sender wallet
    IERC20(path[0]).safeTransferFrom(msg.sender, address(this), amountIn);

    // approve AMM to spend;
    IERC20(path[0]).safeApprove(address(ammRouter), amountIn);

    // Execute Swap
    amounts = ammRouter.swapExactTokensForTokens(amountIn, amountOutMin, path, msg.sender, deadline);
    emit SwapExecuted(msg.sender, amountIn, amounts[amounts.length - 1], path);

    return amounts;
  }

}

